import React, {Component} from 'react';
import {Route} from 'react-router-dom';

import CheckoutSummary from '../../components/Order/CheckoutSummary/CheckoutSummary';
import ContactData from '../Checkout/ContactData/ContactData';



class Checkout extends Component {
    state = {
        ingredients: null,
        price:0,
    }

    componentDidMount () {
        const query = new URLSearchParams(this.props.location.search);
        const ingredients ={};
        let price = 0;
        for (let param of query.entries()){
            //this will return ['salad','1']
            if(param[0]==='price'){
                price = param[1];
            } else {
                ingredients[param[0]]=+param[1];
            }
        }
        this.setState({ingredients: ingredients, totalPrice: price})
    };

    checkoutCancelledHandler = () => (
        this.props.history.goBack()
    );

    checkoutContinueHandler = () => (
        this.props.history.push('checkout/contact-data')
    );


    render(){
        return(
            <div>
                <CheckoutSummary 
                ingredients={this.state.ingredients}
                checkoutCancelled={this.checkoutCancelledHandler} 
                checkoutContinue={this.checkoutContinueHandler}
                />
                <Route path={this.props.match.url + '/contact-data'} render={()=> (<ContactData ingredients={this.state.ingredients}/>)}/>
                {/*as a function and take jsx on the right side 
                Since we are handling it manually through render, we can now pass props*/}
            </div>
        )
    }
}

export default Checkout;
