import * as actionTypes from './actionTypes';

export const addIng = (ingType) => {
    return {
        type: actionTypes.ADD_ING,
        ingType: ingType
    }
}
export const removeIng = (ingType) => {
    return {
        type: actionTypes.REMOVE_ING,
        ingType: ingType
    }
}
